#!/usr/bin/env python# encoding: utf-8import csvfrom pathlib import Pathclass TweetCommonUtil:    def read_info_from_csv(file_name, key_index, nrows=None):        result = {}        file_check = Path(file_name)        if (not file_check.exists()):            return result        with open(file_name, 'r', encoding='utf-8', errors='ignore') as inputFile:            reader = csv.reader(inputFile)            next(reader)            cur_rows = 0            for row in reader:                screen_name = TweetCommonUtil.get_screen_name(row[key_index])                if (screen_name not in result):                    result[screen_name] = row                cur_rows += 1                if nrows == cur_rows:                    break        return result    def to_csv(line, file_name, is_append):        mode = 'w'        if (is_append == True):            mode = 'a'        try:            with open(file_name, mode, encoding='utf-8', errors='ignore', newline='') as outputFile:                writer = csv.writer(outputFile)                writer.writerow(line)        except:            print("write failed-------" + line)    def get_screen_name(inputString):        return inputString[1:]if __name__ == '__main__':    pass